import React, { useRef } from 'react'
import { useNavigate } from 'react-router-dom'
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Img from './Example.png'


const LoginForm = () => {
    const form = useRef();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
      e.preventDefault()
      //Recuperar informaci칩n del form
      const formData = new FormData(form.current)   

      const response = await fetch('http://localhost:4000/login',
                                    { method: 'POST', body: formData}
                                  );
      const data = await response.json();
      if(data.token !== ''){
        localStorage.setItem('token', data.token);
        //sessionStorage.setItem('token', data.token);
        //let fecha = new Date();
        //fecha.setTime(fecha.getTime() + (3600*1000));
        //document.cookie = `token=${data.token}; expires=${fecha.toUTCString()}`;

        navigate('/home');
      }


    }

    return (
      <Container id="main-container" className="d-grid h-100">
        <Form ref={form} onSubmit={handleSubmit}
              id="sign-in-form" className="w-100 text-center">
          <img src="../assets/5.png"
              alt="Logo Higea" className="login-logo mb-4"/>
          <Form.Group className="mb-1">
            <Form.Control type="text" placeholder="Nombre de usuario"
                          name="user" required />
          </Form.Group>
          <Form.Group className="mb-1">
            <Form.Control type="password" placeholder="Contrase침a"
                          name="password" required />
          </Form.Group>
          <Button className="w-100" 
                  type="submit" variant="primary">Iniciar sesi칩n</Button>
        </Form>
      </Container>
    )
}
export default LoginForm;
